interface ProfileModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

const moodEmojis = {
  "very-happy": { emoji: "ðŸ˜„", text: "Harika" },
  "happy": { emoji: "ðŸ˜Š", text: "Ä°yi" },
  "neutral": { emoji: "ðŸ˜", text: "Normal" },
  "sad": { emoji: "ðŸ˜”", text: "ÃœzgÃ¼n" },
  "angry": { emoji: "ðŸ˜ ", text: "Sinirli" },
};

export function ProfileModal({ open, onOpenChange }: ProfileModalProps) {
  const [selectedMood, setSelectedMood] = useState<string>("");
  const [note, setNote] = useState("");
  const { toast } = useToast();

  const { data: latestMood } = useQuery<Mood>({
    queryKey: ["/api/moods/latest"],
    enabled: open,
  });

  const saveMoodMutation = useMutation({
    mutationFn: (data: InsertMood) => 
      apiRequest("POST", "/api/moods", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/moods"] });
      queryClient.invalidateQueries({ queryKey: ["/api/moods/latest"] });
      toast({
        title: "Ruh hali kaydedildi",
        description: "Ruh haliniz baÅŸarÄ±yla kaydedildi.",
      });
      onOpenChange(false);
      setSelectedMood("");
      setNote("");
    },
    onError: () => {
      toast({
        title: "Hata",
        description: "Ruh hali kaydedilemedi.",
        variant: "destructive",
      });
    },
  });

  const handleSave = () => {
    if (!selectedMood) {
      toast({
        title: "UyarÄ±",
        description: "LÃ¼tfen bir ruh hali seÃ§in.",
        variant: "destructive",
      });
      return;
    }

    saveMoodMutation.mutate({
      mood: selectedMood as any,
      note: note.trim() || undefined,
    });
  };

  const formatTimeAgo = (date: Date) => {
    const now = new Date();
    const diffInHours = Math.floor((now.getTime() - new Date(date).getTime()) / (1000 * 60 * 60));

    if (diffInHours < 1) {
      return "Az Ã¶nce";
    } else if (diffInHours < 24) {
      return `${diffInHours} saat Ã¶nce`;
    } else {
      const diffInDays = Math.floor(diffInHours / 24);
      return `${diffInDays} gÃ¼n Ã¶nce`;
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle>Ruh Halini PaylaÅŸ</DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {/* Mood Selection */}
          <div>
            <label className="block text-sm font-medium text-foreground mb-2">
              BugÃ¼n nasÄ±l hissediyorsun?
            </label>
            <div className="grid grid-cols-5 gap-2">
              {Object.entries(moodEmojis).map(([mood, { emoji, text }]) => (
                <button
                  key={mood}
                  onClick={() => setSelectedMood(mood)}
                  className={`p-3 rounded-lg border transition-colors text-center ${
                    selectedMood === mood
                      ? "border-primary bg-primary text-primary-foreground"
                      : "border-border hover:bg-secondary"
                  }`}
                  data-testid={`mood-${mood}`}
                >
                  <div className="text-2xl">{emoji}</div>
                  <div className="text-xs mt-1">{text}</div>
                </button>
              ))}
            </div>
          </div>

          {/* Note */}
          <div>
            <label htmlFor="mood-note" className="block text-sm font-medium text-foreground mb-2">
              Notun
            </label>
            <Textarea
              id="mood-note"
              placeholder="BugÃ¼n nasÄ±l hissediyorsun? Notunu buraya yaz..."
              value={note}
              onChange={(e) => setNote(e.target.value)}
              className="h-24 resize-none"
              data-testid="input-note"
            />
          </div>

          {/* Current Mood Display */}
          {latestMood && (
            <div className="bg-muted rounded-lg p-3">
              <div className="text-sm text-muted-foreground mb-1">Son ruh hali:</div>
              <div className="flex items-center space-x-2">
                <span className="text-lg">
                  {moodEmojis[latestMood.mood as keyof typeof moodEmojis]?.emoji}
                </span>
                <span className="text-sm text-foreground">
                  {moodEmojis[latestMood.mood as keyof typeof moodEmojis]?.text} hissediyorum
                </span>
                <span className="text-xs text-muted-foreground">
                  â€¢ {formatTimeAgo(latestMood.createdAt!)}
                </span>
              </div>
              {latestMood.note && (
                <p className="text-xs text-muted-foreground mt-2 italic">
                  "{latestMood.note}"
                </p>
              )}
            </div>
          )}

          {/* Action Buttons */}
          <div className="flex space-x-3 pt-2">
            <Button 
              onClick={handleSave}
              disabled={saveMoodMutation.isPending}
              className="flex-1"
              data-testid="button-save-mood"
            >
              {saveMoodMutation.isPending ? "Kaydediliyor..." : "Kaydet"}
            </Button>
            <Button 
              variant="secondary"
              onClick={() => onOpenChange(false)}
              className="flex-1"
              data-testid="button-cancel-mood"
            >
              Ä°ptal
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

