  // Calculate AYT total net
  const calculateAYTNet = (): number => {
    return Object.values(aytData).reduce((total, subject) => {
      return total + calculateNet(subject.dogru, subject.yanlis);
    }, 0);
  };

  // Calculate OBP (YKS standard: diploma * 5, max 500)
